$mainColor: #39393F;
$activeColor: #D4B179;
// $activeColor: #f99142;
$tipColor: #ccc;
$disableColor: #d8d8d8;

$mainTxtColor: rgba(0, 0, 0, 0.77);
$subTxtColor: rgba(0, 0, 0, 0.6);
$borderColor: rgba(0, 0, 0, 0.06);
$bgColor: rgba(0, 0, 0, 0.08);
$activeBgColor: #f5a623;
$defaultBgColor: #F6F6F6;
$baseBgColor: #F1F2F8;

$colorRed: #D25C5C;

$boxShadow: 0 4px 8px 0 rgba(77, 95, 169, 0.2);

@mixin flex {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.flex {
  @include flex;

  &.center {
    justify-content: center;
  }
}

.flex.center {
  // background-color: pink;
}

.fixed {
  position: fixed;
}

.form-placeholder {
  color: #ccc;
}

@mixin bg-tip($text: '多宝街', $fonsSize: 44px) {
  position: relative;

  &:after {
    content: $text;
    white-space: nowrap;
    color: #aaa;
    font-size: $fonsSize;
    font-weight: 900;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
}

@mixin bg-transform {
  background: linear-gradient(90deg, #eee 0%, #fefefe 50%, #f6f6f6 100%);
  background-size: 200% 100%;
  background-position: 0 0;
  animation: bgposition 1s infinite linear alternate;
}

@keyframes bgposition {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 100% 0;
  }
}
",'








































@import "@/assets/css/mixin.scss";

.comp-hot-item {
  box-shadow: $boxShadow;
  border-radius: 5px;
  overflow: hidden;
  box-sizing: border-box;

  &.active {
    border: 2px dashed pink;
  }

  p {
    margin: 0;
  }

  .item-image {
    padding-bottom: 100%;
    position: relative;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;

    .item-tip {
      position: absolute;
      left: 0;
      bottom: 0;
      background-color: #757575;
      color: #fff;
      padding: 2px 6px;
      font-size: 12px;
      &.vip {
        background-color: rgba(139, 87, 42, 1);
      }
    }
  }

  .item-body {
    padding: 8px;

    .item-title {
      font-size: 13px;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .item-price {
      font-size: 10px;
      margin: 8px 0;

      &-buy,
      &-buy-tip {
        color: $colorRed;
      }

      &-buy {
        font-size: 12px;
        font-weight: bold;
      }

      .discount {
        background-color: $activeColor;
        border-radius: 2px;
      }

      .item-discount-price,
      .item-discount-tip {
        position: relative;
        color: white;
      }

      .item-discount-tip {
        padding: 0 3px;
      }

      .item-discount-price {
