        font-size: 12px;
        font-weight: bold;
      }

      .discount {
        background-color: $activeColor;
        border-radius: 2px;
      }

      .item-discount-price,
      .item-discount-tip {
        position: relative;
        color: white;
      }

      .item-discount-tip {
        padding: 0 3px;
      }

      .item-discount-price {
        padding: 0 4px;

        &:after {
          content: '...';
          display: inline-block;
          color: white;
          position: absolute;
          left: -1px;
          transform: rotate(90deg);
        }
      }
    }

    .item-option {
      .item-tip {
        font-size: 11px;
        color: $tipColor;
      }

      .item-share {
        font-size: 13px;
        font-weight: bold;
        color: white;
        background-color: $mainColor;
        padding: 4px 6px;
        padding-right: 6px;
        border-radius: 3px;
        box-sizing: border-box;
        width: 60%;
        text-align: center;

        .item-share-tip {
          font-size: 11px;
        }
      }
    }
  }
}
