.comp-category-item {
  .item-head {
    display: flex;
    justify-content: space-between;
    padding: 12px;
  }

  .item-body {
    text-align: left;
  }

  .body-child {
    text-align: center;
    display: inline-block;
    width: 25%;
    padding: 10px;
    box-sizing: border-box;
    font-size: 12px;
    vertical-align: top;
  }

  .child-bg-wrapper {
    width: 60%;
    display: inline-block;
    .child-bg {
      width: 100%;
      padding-bottom: 100%;
      border-radius: 50%;
      background-color: #eee;
      background-position: center;
      background-size: contain;
      background-repeat: no-repeat;
    }
  }

  .child-title {
    margin: 8px 0;
  }

  .fade-enter-active,
  .fade-leave-active {
    transition: opacity 0.2s;
  }
  .fade-enter, .fade-leave-to /* .fade-leave-active below version 2.1.8 */ {
    opacity: 0;
  }
}
