@import '@/assets/css/mixin.scss';

.comp-pyq-item {
  box-shadow: $boxShadow;
  overflow: hidden;
  padding: 12px;
  box-sizing: border-box;

  &.active {
    border: 2px dashed pink;
  }

  p {
    margin: 0;
  }

  .item-image,
  .item-main {
    text-align: left;
    display: inline-block;
    vertical-align: top;
  }

  .item-image {
    position: relative;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    overflow: hidden;
  }

  .item-main {
    margin-left: 8px;
    width: calc(100% - 48px);

    .item-head,
    .item-date {
      font-size: 13px;
    }

    .item-date {
      color: $subTxtColor;
    }

    .item-title {
      font-size: 14px;
      word-break: break-all;
    }

    .item-banner {
      .item-banner-image {
        width: 30%;
        margin: 1%;
        display: inline-block;
        padding-bottom: 30%;
        border: 1px solid #f1f1f1;
        border-radius: 12px;
        background-size: contain;
      }
    }

    .item-price {
      font-size: 14px;
      margin: 8px 0;

      .discount {
        background-color: $activeColor;
        border-radius: 2px;
      }
    }

    .item-detail {
      background-color: #f6f6f6;
      padding: 8px;
      border-radius: 6px;

      .item-detail-info {
        flex: none;
        width: calc(100% - 20px);
      }

      &-image {
        width: 50px;
        height: 50px;
      }

      .item-info {
        height: 50px;
        margin-left: 8px;
        width: calc(100% - 58px);
        font-size: 13px;
        color: $subTxtColor;
        flex-direction: column;
        align-items: flex-start;
        justify-content: space-between;

        .item-text {
          width: 100%;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }

        .item-price {
          font-size: inherit;
          margin: 0;
        }
      }
    }

    .item-share {
      margin: 8px;

      .share-price {
        font-size: 15px;
        font-weight: bold;
        color: $colorRed;
      }

      .share-count {
        font-size: 12px;
        color: $mainTxtColor;
      }
    }
    .item-foot {
      text-align: right;
    }
  }
}
