.mobile-search {
  height: 52px;
  color: #666;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #fff;
  width: 100vw;
  padding-left: 15px;
  overflow: hidden;
  box-shadow: inset 0 -1px 0 0 #e5e5e5;
  box-sizing: border-box;

  .search-list {
    white-space: nowrap;
    overflow: auto;
    font-size: 12px;
  }

  .list-item {
    padding: 5px 8px;
    margin: 4px;
    border-radius: 4px;
    background-color: #f4f4f4;
    display: inline-block;
    border: 0.5px solid transparent;
    &.active {
      border-radius: 4px;
      background-color: #e9f4ff;
      border-color: #c1e0ff;
      color: #0080fe;
    }
  }

  .icon-search {
    flex: none;
    padding: 16px 17px;

    &.btn {
      box-shadow: -1px 0 10px #ddd;
      z-index: 1;
    }
  }

  .van-search {
    padding: 0;
    flex: 1;
    .van-search__action {
      padding: 0 12px;
    }
  }

  .fade-enter-active,
  .fade-leave-active {
    transition: opacity 0.5s;
  }
  .fade-enter, .fade-leave-to /* .fade-leave-active below version 2.1.8 */ {
    opacity: 0;
  }
}
