const loopRoute = (basePath: string, e: Array<RouteConfig>) => {
  let arr: Array<string> = [];

  e.map(d => {
    const { path: pn, children } = d;

    if (!children) {
      const baseP = basePath === '/' ? '' : basePath;
      const bPn = pn === '/' ? '' : pn;

      return arr.push(
        `${baseP}${basePath === '/' || pn === '/' ? '' : '/'}${bPn}`
      );
    }

    arr = arr.concat(loopRoute(pn, children));
  });

  return arr;
};

